CREATE TABLE User (id INT NOT NULL AUTO_INCREMENT,first_name VARCHAR(30) NULL,last_name VARCHAR(50) NULL,user_name VARCHAR(20) NULL,email VARCHAR(40) NULL,password VARCHAR(100) NULL,is_admin BOOLEAN NOT NULL DEFAULT false,PRIMARY KEY ( id ))ENGINE=INNODB;
CREATE TABLE ConnectionRequest (id INT NOT NULL AUTO_INCREMENT,userId INT NOT NULL ,telephone VARCHAR(12) NULL ,status VARCHAR(50) NULL ,address1 VARCHAR(30) NULL,address2 VARCHAR(30) NOT NULL ,street VARCHAR(30) NULL ,city VARCHAR(30) NOT NULL ,district VARCHAR(30) NOT NULL ,PRIMARY KEY (id),FOREIGN KEY (userId) REFERENCES User(id))ENGINE=INNODB;